import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const HousingEdit = () => {
  const navigate = useNavigate();
  const [id, setId] = useState("");
  const [newHousePlan, setNewHousePlan] = useState("");
  const [newNumDeed, setNewNumDeed] = useState("");
  const [newLandArea, setNewLandArea] = useState("");
  const [newPrice, setNewPrice] = useState("");
  const [newNote, setNewNote] = useState("");
  const [newStatus, setNewStatus] = useState("");
  const [newImage, setNewImage] = useState(null);
  const [newidFK, setNewidFK] = useState("");

  const editHouse = async () => {
    // ตรวจสอบข้อมูลก่อนส่งคำขอ PATCH
    if (!id || !newHousePlan || !newNumDeed || !newLandArea || !newPrice || !newNote || !newStatus || !newidFK) {
      alert("กรุณากรอกข้อมูลให้ครบทุกช่อง");
      return;
    }

    const formData = new FormData();
    formData.append("id", id);
    formData.append("newHousePlan", newHousePlan);
    formData.append("newNumDeed", newNumDeed);
    formData.append("newLandArea", newLandArea);
    formData.append("newPrice", newPrice);
    formData.append("newNote", newNote);
    formData.append("newStatus", newStatus);
    formData.append("newImage", newImage);
    formData.append("newidFK", newidFK);

    try {
      const response = await axios.patch('http://26.90.237.200:3000/admin/house/edit', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log(response.data);
      navigate('/Housing');
    } catch (error) {
      console.error(error);
      navigate('/Error');
    }
  };

  return (
    <div className="container">
      <form>
        <div className="mb-3">
          <div>
            <label htmlFor="id" className="form-label">
              รหัส
            </label>
            <input
              type="text"
              className="form-control"
              id="id"
              value={id}
              onChange={(e) => setId(e.target.value)}
            />
          </div>
          {/* ฟิลด์อื่น ๆ */}
        </div>
        <button type="button" className="btn btn-primary" onClick={editHouse}>
          เพิ่มบ้าน
        </button>
      </form>
    </div>
  );
};

export default HousingEdit;
