import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';

const Receipt = () => {
  const [books, setBooks] = useState([]);
  const Auth = useContext(AuthContext);

  useEffect(() => {
    loadBooking();
  }, []);

  const loadBooking = () => {
    axios.get('http://26.90.237.200:3000/book/read')
      .then(response => {
        setBooks(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handlePrint = () => {
    window.print();
  };

  return (
    <div style={{ backgroundImage: `url("/image/Receipt.png")`, backgroundSize: 'cover', backgroundRepeat: 'no-repeat', backgroundPosition: 'center', height: '100vh', width: '100vw' }}>
  {/* ตรงนี้คือเนื้อหาภายในของ div ถ้าต้องการแสดงข้อมูลอื่นๆ ในหน้านี้ให้เพิ่มเติมตรงนี้ */}
      <br />
      <div>
      {/* <img src="/image/Receipt.png" alt="ภาพ PNG" style={{ height: 'auto', width: '100%' }} /> */}
        {books.map((book, idx) => (
          <div className="" key={idx}>
            <div className="content">
              <p>เลขการจอง: {book.b_id}</p>
              <p>ราคา: {book.amount}</p>
              <p>วันที่จอง: {book.date}</p>

              <div></div>
              <p>บ้านเลขที่: {book.h_id}</p>
              <p>เลขบัตรประชาชนผู้จอง: {book.user_id}</p>
            </div>
            <div className="content">
              <button type="button" className="btn btn-primary" onClick={handlePrint}>
                แก้ไขข้อมูล
              </button>
              <br />
              <br />
              <br />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Receipt;
