import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { NumericFormat } from 'react-number-format';
import { useNavigate } from 'react-router-dom';

const ShowHousing = () => {
  const Auth = useContext(AuthContext);
  const [houses, setHouses] = useState([]);
  const [types, setTypes] = useState([]);
  const [selectedType, setSelectedType] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    loadHouses();
    loadHouseTypes();
  }, []);

  const loadHouses = () => {
    axios.get('http://26.90.237.200:3000/admin/house/read')
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const loadHouseTypes = () => {
    axios.get('http://26.90.237.200:3000/admin/house_type/read')
      .then(response => {
        setTypes(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleShowAllClick = () => {
    loadHouses();
    setSelectedType(null);
  };
  
  const handleTypeClick = (type) => {
    const data = { id: type.ht_id };
    setSelectedType(data);
    axios.post('http://26.90.237.200:3000/admin/house/readtype/', data)
      .then(response => {
        setHouses(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const deleteHouse = (house) => {
    const confirmDelete = window.confirm('คุณต้องการลบบ้านนี้หรือไม่?');

    if (confirmDelete) {
      const data = { id: house.h_id };

      axios.delete('http://26.90.237.200:3000/admin/house/delete/', {
        data: JSON.stringify(data),
        headers: {
          'Content-Type': 'application/json'
        }
      })
        .then(response => {
          alert('ลบบ้านสำเร็จ');
          loadHouses(); // โหลดข้อมูลบ้านใหม่หลังจากลบสำเร็จ
        })
        .catch(error => {
          console.error(error);
          alert('เกิดข้อผิดพลาดในการลบบ้าน');
        });
    }
  };

  return (
    <div className="container">
      {/* ส่วนอื่น ๆ ในการแสดงผล */}
      {/* ... */}

      <div>
        {houses.map((house, idx) => (
          <div className="card" key={idx}>
            {/* ส่วนอื่น ๆ ในการแสดงผล */}
            {/* ... */}

            <div className="content">
              <button className="btn btn-danger" onClick={() => deleteHouse(house)}>
                ลบ
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ShowHousing;
