import React, { useState, useContext } from 'react';
import { AuthContext } from './AuthContext';
import loginUser from './Login';
import { useNavigate } from 'react-router-dom';

const LoginForm = () => {
  const dxd = useContext(AuthContext);
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await loginUser(username, password);
    console.log(response)
    if (response.auth) {
      dxd.setIsLoggedIn(true);
      localStorage.setItem('loggedIn', 'true');
      // ถ้า status มีค่าเป็น 1, 2, 3 ให้ไปที่เพจ 'Housing'
      if ([1, 2, 3].includes(response.status)) {
        navigate('/Housing');
      } else {
        navigate('/SomeOtherPage'); // เปลี่ยน '/SomeOtherPage' ไปยัง path ที่คุณต้องการให้ผู้ใช้ไปถ้า status ไม่ใช่ 1, 2, 3
      }
    } else {
      dxd.setIsLoggedIn(false);
      localStorage.setItem('loggedIn', 'false');
      navigate('/');
    }
  };

  return (
    <div className="container">
    <h1>Login Form</h1>
    <form onSubmit={handleSubmit}>
      <div className="mb-3">
        <label htmlFor="username" className="form-label">Username</label>
        <input
          type="text"
          className="form-control"
          id="username"
          name="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <label htmlFor="password" className="form-label">Password</label>
        <input
          type="password"
          className="form-control"
          id="password"
          name="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <button type="submit" className="btn btn-primary">Login</button>
      </div>
    </form>
  </div>
  );
};

export default LoginForm;
