    import React, { useState, useEffect } from 'react';
    import axios from 'axios';
    import { useNavigate, useParams } from "react-router-dom";
    import { Link } from 'react-router-dom';

    const AddBooking = () => {
        const navigate = useNavigate();
        const House_API = 'http://26.90.237.200:3000/book/add';
        const [books, setBooks] = useState([]);
        const [addedBookData, setAddedBookData] = useState(null);

        const [addName, setAddName] = useState('');
        const [addLastname, setAddLastname] = useState('');
        const [addAddress, setAddAddress] = useState('');
        const [addAge, setAddAge] = useState('');
        const [addPhone, setAddPhone] = useState('');
        const [addHouseFK, setAddHouseFK] = useState('');
        const [addUserFK, setAddUserFK] = useState('');
      
        useEffect(() => {
            loadBooking();
          }, []);

          const loadBooking = () => {
            axios.get('http://26.90.237.200:3000/book/read')
              .then(response => {
                setBooks(response.data);
              })
              .catch(error => {
                console.error(error);
              });
          };
        
        const addUser = async () => {
            // if (!addName || !addLastname || !addAddress || !addAge || !addPhone || !addHouseFK || !addUserFK) {
            //   alert('กรุณากรอกข้อมูลให้ครบถ้วน');
            //   return;
            // }
        
            // // ตรวจสอบเบอร์โทรศัพท์ที่กรอกเป็นตัวเลขทั้งหมด
            // if (isNaN(addPhone)) {
            //   alert('กรุณากรอกเบอร์โทรศัพท์เป็นตัวเลขเท่านั้น');
            //   return;
            // }
        
            // // แสดงข้อความยืนยันการจอง
            // const confirmBooking = window.confirm('คุณต้องการทำการจองใช่หรือไม่?');
            // if (!confirmBooking) {
            //   return;
            // }
        
            const data = {
              addName: addName,
              addLastname: addLastname,
              addAddress: addAddress,
              addAge: addAge,
              addPhone: addPhone,
              addHouseFK: addHouseFK,
              addUserFK: addUserFK,
            };
            console.log(data);
            try {
              const response = await axios.post(House_API, data);
              console.log(response.data);
                setAddedBookData(response.data);
              alert('ทำการจองสำเร็จแล้ว');
            //   navigate('/Receipt');
            } catch (error) {
              console.error(error);
              alert('ทำการจองผิดพลาด');
            //   navigate('/Booking');
            }
    
          };
          const renderAddedBookData = () => {
            if (!addedBookData) {
              return null;
            }
            return (
              <div>
                <p>บ้านเลขที่: {addedBookData.id}</p>
                {/* แสดงข้อมูลอื่น ๆ ตามที่ต้องการ */}
              </div>
            );
          };
          
// console.log(addedBookData)
          return (
        
        <div className="container">
               <h1>เพิ่มการจอง</h1>
                 {renderAddedBookData()}
        <form >
            <div className="mb-3">
            {/* <p>บ้านเลขที่: {addedBookData.h_id}</p> */}
                <br/>
            <div className="card container">
            <br/>
                <h4>ข้อมูลลูกค้า</h4>
                <br/>
            <div>
                <label htmlFor="id" className="form-label">
                เลขบัตรประชาชน
                </label>
                <input
                type="text"
                className="form-control"
                id="id"
                value={addUserFK}
                onChange={(e) => setAddUserFK(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="addName" className="form-label">
                ชื่อ
                </label>
                <input
                type="text"
                className="form-control"
                id="addName"
                value={addName}
                onChange={(e) => setAddName(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="addLastname" className="form-label">
                นามสกุล
                </label>
                <input
                type="text"
                className="form-control"
                id="addLastname"
                value={addLastname}
                onChange={(e) => setAddLastname(e.target.value)}
                />
            </div>
            <div >
                <label htmlFor="addAddress" className="form-label">
                ที่อยู่
                </label>
                <input
                type="text"
                className="form-control"
                id="addAddress"
                value={addAddress}
                onChange={(e) => setAddAddress(e.target.value)}
                />
            </div>
            
            <div>
                <label htmlFor="addAge" className="form-label">
                อายุ
                </label>
                <input
                type="text"
                className="form-control"
                id="addAge"
                value={addAge}
                onChange={(e) => setAddAge(e.target.value)}
                />
            </div>

            <div>
                <label htmlFor="addPhone" className="form-label">
                เบอร์โทร
                </label>
                <input
                type="text"
                className="form-control"
                id="addPhone"
                value={addPhone}
                onChange={(e) => setAddPhone(e.target.value)}
                />
            </div>
            <br/>
            </div>
            </div>

            <div className="card container" >
            <br/>
                <h4>ข้อมูลบ้านที่จอง</h4>
                <br/>

            <div className="form-group">
            <label>บ้านเลขที่</label>
            <input
                type="text"
                className="form-control"
                value={addHouseFK}
                onChange={(e) => setAddHouseFK(e.target.value)}
            />
            </div>
            <br/>
          
            </div>
            <br/>
            <button type="button" className="btn btn-primary" onClick={addUser}>
            จอง
            </button>
       
            {books.map((book, idx) => (
                    <div className="card" key={idx}>
                    <div className="content">
                      <Link to={`/Receipt/${book.b_id}`} onClick={addUser} className="btn btn-warning">  จอง </Link>
                    </div>
                    </div>
             ))}

        </form>
        </div>
    );
    };
    export default AddBooking;
