    import React, { useState,useEffect } from 'react';
    import axios from 'axios';
    import { useNavigate } from "react-router-dom";
    import { Link } from 'react-router-dom';
    import { DropdownButton, Dropdown } from 'react-bootstrap';

    const AddBooking = () => {
        const navigate = useNavigate();
        const House_API = 'http://26.90.237.200:3000/book/add';
        const [addedBookData, setAddedBookData] = useState('');

        const [addName, setAddName] = useState('');
        const [addLastname, setAddLastname] = useState('');
        const [addAddress, setAddAddress] = useState('');
        const [addAge, setAddAge] = useState('');
        const [addPhone, setAddPhone] = useState('');
        const [addHouseFK, setAddHouseFK] = useState('');
        const [addUserFK, setAddUserFK] = useState('');

        const [houses, setHouses] = useState([]);

        useEffect(() => {
          loadHouses();
        }, []);
      
        const loadHouses = () => {
          axios
            .get('http://26.90.237.200:3000/admin/house/read')
            .then(response => {
              setHouses(response.data);
            })
            .catch(error => {
              console.error(error);
            });
        };
        console.log(houses);
      
        const AddBook = async () => {
            
            if (!addName || !addLastname || !addAddress || !addAge || !addPhone || !addHouseFK || !addUserFK) {
              alert('กรุณากรอกข้อมูลให้ครบถ้วน');
              return;
            }
        
            // ตรวจสอบเบอร์โทรศัพท์ที่กรอกเป็นตัวเลขทั้งหมด
            if (isNaN(addPhone)) {
              alert('กรุณากรอกเบอร์โทรศัพท์เป็นตัวเลขเท่านั้น');
              return;
            }
        
            // แสดงข้อความยืนยันการจอง
            const confirmBooking = window.confirm('คุณต้องการทำการจองใช่หรือไม่?');
            if (!confirmBooking) {
              return;
            }
        
            const data = {
              addName: addName,
              addLastname: addLastname,
              addAddress: addAddress,
              addAge: addAge,
              addPhone: addPhone,
              addHouseFK: addHouseFK,
              addUserFK: addUserFK,
            };
            try {
                const response = await axios.post(House_API, data);
                setAddedBookData(response.data);
                alert('ทำการจองสำเร็จแล้ว');
          
                // ทำการ navigate ไปยังหน้า Receipt พร้อมกับส่งค่า addedBookData ไปด้วย
                navigate(`/Receipt/${response.data.id}`);
              } catch (error) {
                console.error(error);
                alert('ทำการจองผิดพลาด');
              }
            };
          return (
        
        <div className="container">
               <h1>เพิ่มการจอง</h1>
        <form >
            <div className="mb-3">
                <br/>
            <div className="card container">
            <br/>
                <h4>ข้อมูลลูกค้า</h4>
                <br/>
            <div>
                <label htmlFor="id" className="form-label">
                เลขบัตรประชาชน
                </label>
                <input
                type="text"
                className="form-control"
                id="id"
                value={addUserFK}
                onChange={(e) => setAddUserFK(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="addName" className="form-label">
                ชื่อ
                </label>
                <input
                type="text"
                className="form-control"
                id="addName"
                value={addName}
                onChange={(e) => setAddName(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="addLastname" className="form-label">
                นามสกุล
                </label>
                <input
                type="text"
                className="form-control"
                id="addLastname"
                value={addLastname}
                onChange={(e) => setAddLastname(e.target.value)}
                />
            </div>
            <div >
                <label htmlFor="addAddress" className="form-label">
                ที่อยู่
                </label>
                <input
                type="text"
                className="form-control"
                id="addAddress"
                value={addAddress}
                onChange={(e) => setAddAddress(e.target.value)}
                />
            </div>
            
            <div>
                <label htmlFor="addAge" className="form-label">
                อายุ
                </label>
                <input
                type="text"
                className="form-control"
                id="addAge"
                value={addAge}
                onChange={(e) => setAddAge(e.target.value)}
                />
            </div>

            <div>
                <label htmlFor="addPhone" className="form-label">
                เบอร์โทร
                </label>
                <input
                type="text"
                className="form-control"
                id="addPhone"
                value={addPhone}
                onChange={(e) => setAddPhone(e.target.value)}
                />
            </div>
            <br/>
            </div>
            </div>

            <div className="card container" >
            <br/>
                <h4>ข้อมูลบ้านที่จอง</h4>
                <br/>

            <div className="form-group">
            <label>บ้านเลขที่</label>
            <input
                type="text"
                className="form-control"
                value={addHouseFK}
                onChange={(e) => setAddHouseFK(e.target.value)}
            />
            </div>

            <div>
            <label htmlFor="addidFK" className="form-label">
            บ้านเลขที่
            </label>
            <DropdownButton
              id="addidFK"
              title={addHouseFK !== 0 ? houses.find(zone => zone.hz_id === addidFK)?.name : 'เลือกโซนบ้าน'}
              onSelect={e => setAddidFK(parseInt(e))}
            >
              {houses.map((house, idx) => (
                <Dropdown.Item key={idx} eventKey={house.h_id}>
                  {house.name}
                </Dropdown.Item>
              ))}
            </DropdownButton>
          </div>

            </div>

            <br/>
                <div className="content">
                    <Link  onClick={AddBook} className="btn btn-primary">  จอง </Link>
                </div>
            <br/>
        </form>
        </div>
    );
    };
    export default AddBooking;
