import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ZoneEdit = () => {
  const House_API = "http://26.90.237.200:3000/admin/house_zone/edit";
  const navigate = useNavigate();
  
  const [id, setId] = useState("");
  const [newName, setNewName] = useState("");

  const updateEmp = async () => {
    const data = {
      id: id,
      newName: newName,
    };

    try {
      const response = await axios.patch(House_API, data);
      console.log(response.data);
      navigate('/Housing');
    } catch (error) {
      console.error(error);
      navigate('/Housing');
    }
  };

  return (
    <div className="container">
         <h1>แก้ไขโซนที่ดิน</h1>
      <form>
        
        <div className="form-group">

          <label>รหัส</label>
          <input
            type="text"
            className="form-control"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>ชื่อใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
        </div>

        <button type="button" className="btn btn-primary" onClick={updateEmp}>
          แก้ไขข้อมูล
        </button>
      </form>
    </div>
  );
};

export default ZoneEdit;
