import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const EditUser = () => {
  const User_API = "http://26.90.237.200:3000/user/edit";
  const navigate = useNavigate();

  const [id, setId] = useState("");
  const [newName, setNewName] = useState("");
  const [newLastname, setNewLastname] = useState("");
  const [newAddress, setNewAddress] = useState("");
  const [newAge, setNewAge] = useState(0);
  const [newPhone, setNewPhone] = useState("");
  const [success, setSuccess] = useState(false);

  const updateUser = async () => {
    const data = {
      id: id,
      newName: newName,
      newLastname: newLastname,
      newAddress: newAddress,
      newAge: newAge,
      newPhone: newPhone,
    };

    try {
      const response = await axios.patch(User_API, data);
      console.log(response.data);
      setSuccess(true);
      navigate('/User');
    } catch (error) {
      console.error(error);
      setSuccess(false);
      navigate('/User');
    }
  };

  return (
    <div>
      <form>
        <div className="form-group">
          <label>รหัสผู้ใช้</label>
          <input
            type="text"
            className="form-control"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>ชื่อใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>นามสกุลใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newLastname}
            onChange={(e) => setNewLastname(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>ที่อยู่ใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newAddress}
            onChange={(e) => setNewAddress(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>อายุใหม่</label>
          <input
            type="number"
            className="form-control"
            value={newAge}
            onChange={(e) => setNewAge(parseInt(e.target.value))}
          />
        </div>
        <div className="form-group">
          <label>เบอร์โทรศัพท์ใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newPhone}
            onChange={(e) => setNewPhone(e.target.value)}
          />
        </div>

        <button type="button" className="btn btn-primary" onClick={updateUser}>
          แก้ไขข้อมูล
        </button>

        {success && <p className="text-success">แก้ไขข้อมูลสำเร็จ</p>}
        {!success && <p className="text-danger">เกิดข้อผิดพลาดในการแก้ไขข้อมูล</p>}
      </form>
    </div>
  );
};

export default EditUser;
