import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from "react-router-dom";
import { PDFDocument, rgb } from 'pdf-lib';
import dayjs from 'dayjs';
import fontkit from '@pdf-lib/fontkit';
import THSarabunBold from '../../fonts/THSarabunBold.ttf'; // ปรับปรุงพาธตามที่เหมาะสม

const Receipt = () => {
  const [book, setBook] = useState({ b_id: '', user_id: '', h_id: '', date: '' });
  const [user, setUser] = useState({ user_name: '', user_lastname: '', user_phone: '', user_age: '' });
  const [house, setHouse] = useState({ id: '', land_area: '', house_plan: '', book_price: '' });
  const navigate = useNavigate();

  const { Bid } = useParams();

  useEffect(() => {
    loadBookingData();
  }, []);

  const loadBookingData = async () => {
    try {
      const response = await axios.post(`http://26.90.237.200:3000/book/read/id`, { id: Bid });
      setBook(response.data[0]);
      loadUserData(response.data[0].user_id);
      loadHouseData(response.data[0].h_id);
    } catch (error) {
      console.error(error);
    }
  };

  const loadUserData = async (userId) => {
    try {
      const response = await axios.post(`http://26.90.237.200:3000/user/read/id`, { id: userId });
      setUser(response.data[0]);
    } catch (error) {
      console.error(error);
    }
  };

  const loadHouseData = async (houseId) => {
    try {
      const response = await axios.post(`http://26.90.237.200:3000/admin/house/read/id`, { id: houseId });
      setHouse(response.data[0]);
    } catch (error) {
      console.error(error);
    }
  };

  // ... generatePdf and other functions ...

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBook(prevBook => ({ ...prevBook, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.patch(`http://26.90.237.200:3000/book/edit/${book.b_id}`, book);
      window.alert('แก้ไขข้อมูลสำเร็จ');
      navigate('/Booking');
    } catch (error) {
      console.error(error);
      window.alert('เกิดข้อผิดพลาดในการแก้ไขข้อมูล');
      navigate('/Error');
    }
  };

  return (
    <div className="container">
      <h1>แก้ไขการจอง</h1>
      <form onSubmit={handleSubmit}>
        {/* Form fields to edit the booking, user, and house data */}
        <div className="form-group">
          <label>เลขการจอง</label>
          <input type="text" name="b_id" value={book.b_id} onChange={handleInputChange} />
        </div>
        {/* Additional form fields for the other properties... */}
        <button type="submit">แก้ไขข้อมูล</button>
      </form>
    </div>
  );
};

export default Receipt;
