import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { useNavigate } from 'react-router-dom';

const ProfileEdit = () => {
  const navigate = useNavigate();
  const [aboutUs, setAboutUs] = useState([]);
  const [formData, setFormData] = useState({
    
    name: '',
    address: '',
    manager_name: '',
    manager_lastname: '',
    he_username: '',
    he_password: '',
    down_rate: '',
    common_money: '',
  });
  const Auth = useContext(AuthContext);

  useEffect(() => {
    axios
      .get('http://26.90.237.200:3000/admin/read')
      .then((response) => {
        setAboutUs(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // ส่งคำขอแก้ไขข้อมูลไปยัง API
    axios
      .patch('http://26.90.237.200:3000/admin/edit', formData)
      .then((response) => {
        console.log(response.data);
        // อัปเดตข้อมูลที่แก้ไขล่าสุดใน aboutUs state
        const updatedAboutUs = aboutUs.map((item) => {
          if (item._id === response.data._id) {
            return response.data;
          } else {
            return item;
          }
        });
        setAboutUs(updatedAboutUs);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div className="container">
      <div>
        {aboutUs.map((val, idx) => (
          <div className="" key={idx}>
            <div className="content">
              <br />
              <br />
              <div className=''>
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="name" className="form-label">
                    โครงการ
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    name="name"
                    value={formData.name || val.name}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="address" className="form-label">
                    ที่อยู่
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="address"
                    name="address"
                    value={formData.address || val.address}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="manager_name" className="form-label">
                    ชื่อ
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="manager_name"
                    name="manager_name"
                    value={formData.manager_name || val.manager_name}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="manager_lastname" className="form-label">
                    นามสกุล
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="manager_lastname"
                    name="manager_lastname"
                    value={formData.manager_lastname || val.manager_lastname}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="he_username" className="form-label">
                    ชื่อบัญชีผู้ใช้
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="he_username"
                    name="he_username"
                    value={formData.he_username || val.he_username}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="he_password" className="form-label">
                    รหัสผ่าน
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="he_password"
                    name="he_password"
                    value={formData.he_password || val.he_password}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="down_rate" className="form-label">
                    อัตราผ่อน
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="down_rate"
                    name="down_rate"
                    value={formData.down_rate || val.down_rate}
                    onChange={handleInputChange}
                  />
                </div>
              
                <button type="submit" className="btn btn-primary">
                  Save Changes
                </button>
              </form>
              </div>
            </div>
            <div className="content">
              <button
                className="btn btn-danger"
                onClick={() => {
                  navigate('/UserProfile');
                }}
              >
                กลับ
              </button>
              <br />
              <br />
              <br />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProfileEdit;
