import React, { useState } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const EditUser = () => {
  const User_API = "http://26.90.237.200:3000/user/edit";
  const navigate = useNavigate();
  const { Uid } = useParams();
  const [user, setUser] = useState([]);

  const [id, setId] = useState("");
  const [newName, setNewName] = useState("");
  const [newLastname, setNewLastname] = useState("");
  const [newAddress, setNewAddress] = useState("");
  const [newAge, setNewAge] = useState(0);
  const [newPhone, setNewPhone] = useState("");

  useEffect(() => {
    loadUserData();
  }, []);

  const userData = { id: Uid };
// console.log(data);
  const loadUserData = () => {
    axios.post(`http://26.90.237.200:3000/admin/house_zone/read/id`,userData)
      .then(response => {
        setUser(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const updateUser = async () => {
    const data = {
      id: Uid || user.user_id,
      newName: newName || user.user_name,
      newLastname: newLastname || user.user_lastname,
      newAddress: newAddress || user.user_address ,
      newAge: newAge || user.user_age ,
      newPhone: newPhone || user.name,
    };

    // ถามผู้ใช้ก่อนทำการแก้ไข
    const confirmUpdate = window.confirm("คุณต้องการทำการแก้ไขข้อมูลหรือไม่?");

    if (confirmUpdate) {
      try {
        const response = await axios.patch(User_API, data);
        console.log(response.data);

        // แจ้งเตือนเมื่อแก้ไขเสร็จสิ้น
        window.alert("แก้ไขข้อมูลสำเร็จ");
        navigate("/User");
      } catch (error) {
        console.error(error);

        // แจ้งเตือนเมื่อเกิดข้อผิดพลาดในการแก้ไขข้อมูล
        window.alert("เกิดข้อผิดพลาดในการแก้ไขข้อมูล");
        navigate("/User");
      }
    }
  };

  return (
    <div className="container">
          <h1>แก้ไขข้อมูลผู้ลูกค้า</h1>
      <form>
        <div className="form-group">
          <label>เลขบัตรประชาชน</label>
          <input
            type="text"
            className="form-control"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>ชื่อใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>นามสกุลใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newLastname}
            onChange={(e) => setNewLastname(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>ที่อยู่ใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newAddress}
            onChange={(e) => setNewAddress(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>อายุใหม่</label>
          <input
            type="number"
            className="form-control"
            value={newAge}
            onChange={(e) => setNewAge(parseInt(e.target.value))}
          />
        </div>
        <div className="form-group">
          <label>เบอร์โทรศัพท์ใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newPhone}
            onChange={(e) => setNewPhone(e.target.value)}
          />
        </div>
<br/>
        <button type="button" className="btn btn-primary" onClick={updateUser}>
          แก้ไขข้อมูล
        </button>
      </form>
    </div>
  );
};

export default EditUser;
