import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { useNavigate } from 'react-router-dom';

const ProfileEdit = () => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState([]);
  const Auth = useContext(AuthContext);

  useEffect(() => {
    axios.get('http://26.90.237.200:3000/admin/read')
      .then(response => {
        setProfile(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleConfirm = () => {
    // ส่งคำขอแก้ไขโปรไฟล์ไปยัง API
    const data = {
      id: profile[0].id,
      newMName: 'รุ่งรัตน์',
      newMLname: 'ชูศักดิ์',
      newUsername: 'admin',
      newPassword: 'admin',
      newDownRate: 20,
      newName: 'พวงเพชร 4',
      newAddress: '44 หมู่ 11 ตำบลบ้านเป็ด อำเภอเมือง จังหวัดขอนแก่น 40000',
      newCommonMoney: 0
    };

    axios.patch('http://26.90.237.200:3000/admin/edit', data)
      .then(response => {
        console.log(response.data);
        navigate('/UserProfile');
      })
      .catch(error => {
        console.error(error);
        navigate('/UserProfile');
      });
  };

  return (
    <div className="container">
      <div>
        {profile.map((val, idx) => (
          <div className="card" key={idx}>
            <div className="content">
              <br />
              <br />

              <p>โครงการ: {val.name}</p>
              <p>ที่อยู่: {val.address}</p>
              <p>ชื่อ: {val.manager_name}</p>
              <p>นามสกุล: {val.manager_lastname}</p>
              <p>ชื่อบัญชีผู้ใช้: {val.he_username}</p>
              <p>รหัสผ่าน: {val.he_password}</p>
              <p>อัตตราผ่อน: {val.down_rate}%</p>
              <p>เงินส่วนกลาง: {val.common_money}</p>

            </div>
            <div className="content">
              {Auth.isLoggedIn && (
                <button className="btn btn-primary" onClick={handleConfirm}>ยืนยัน</button>
              )}
              {Auth.isLoggedIn && (
                <button className="btn btn-danger" onClick={() => { navigate('/UserProfile'); }}>ยกเลิก</button>
              )}
              <br />
              <br />
              <br />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProfileEdit;
