import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const EditBooking = () => {
  const User_API = "http://26.90.237.200:3000/user/edit";
  const navigate = useNavigate();

  const [id, setId] = useState("");
  const [newAmount, setNewAmount] = useState("");
  const [newDate, setNewDate] = useState("");
  const [newHouseFK, setNewHouseFK] = useState(0);
  const [newUserFK, setNewUserFK] = useState("");

//   "id": 45,
//   "newAmount": "8400",
//   "newDate": "2024-07-15",
//   "newHouseFK": "44/264",
//   "newUserFK": "1234567895423"

  const updateUser = async () => {
    const data = {
      id: id,
      newAmount: newAmount,
      newDate: newDate,
      newHouseFK: newHouseFK,
      newUserFK: newUserFK,
    };

    // ถามผู้ใช้ก่อนทำการแก้ไข
    const confirmUpdate = window.confirm("คุณต้องการทำการแก้ไขข้อมูลหรือไม่?");

    if (confirmUpdate) {
      try {
        const response = await axios.patch(User_API, data);
        console.log(response.data);

        // แจ้งเตือนเมื่อแก้ไขเสร็จสิ้น
        window.alert("แก้ไขข้อมูลสำเร็จ");
        navigate("/User");
      } catch (error) {
        console.error(error);

        // แจ้งเตือนเมื่อเกิดข้อผิดพลาดในการแก้ไขข้อมูล
        window.alert("เกิดข้อผิดพลาดในการแก้ไขข้อมูล");
        navigate("/User");
      }
    }
  };

  return (
    <div>
      <form>
        <div className="form-group">
          <label>รหัสผู้ใช้</label>
          <input
            type="text"
            className="form-control"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>ชื่อใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>นามสกุลใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newLastname}
            onChange={(e) => setNewLastname(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>ที่อยู่ใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newAddress}
            onChange={(e) => setNewAddress(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>อายุใหม่</label>
          <input
            type="number"
            className="form-control"
            value={newAge}
            onChange={(e) => setNewAge(parseInt(e.target.value))}
          />
        </div>
        <div className="form-group">
          <label>เบอร์โทรศัพท์ใหม่</label>
          <input
            type="text"
            className="form-control"
            value={newPhone}
            onChange={(e) => setNewPhone(e.target.value)}
          />
        </div>

        <button type="button" className="btn btn-primary" onClick={updateUser}>
          แก้ไขข้อมูล
        </button>
      </form>
    </div>
  );
};

export default EditBooking;
