import React, { useEffect, useState,useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';


const BookingCard = () => {
  const [books, setBooks] = useState([]);
  const navigate = useNavigate();
  const Auth = useContext(AuthContext);

  useEffect(() => {
    axios.get('http://26.90.237.200:3000/book/read')
      .then(response => {
        setBooks(response.data);
        
      })
      .catch(error => {
        console.error(error);
      });   
  }, []);

  const getStatusMessageAndClass = (book) => {
    switch (status) {
      case 0:
        return { message: 'กำลังดำเนินการจอง', className: 'book-status-pending' };
      case 1:
        return { message: 'การจองเสร็จสิ้น', className: 'book-status-confirmed' };
      case 2:
        return { message: 'ยกเลิกการจอง', className: 'book-status-canceled' };
      default:
        return { message: 'ไม่ทราบสถานะ', className: '' };
    }
  };


  const handleDeleteClick = (book) => {
    // console.log(book)
    const confirmDelete = window.confirm('คุณต้องการการจองนี้หรือไม่?');
    if (confirmDelete) {
      // const data = { id: book.b_id };
      console.log(book.b_id)
      axios.patch(`http://26.90.237.200:3000/book/edit/status`,{
          data: { id: book.b_id },
          headers: {
            'Content-Type': 'application/json'
          }
        })
        .then(response => {
          alert('ยกเลิกการจองสำเร็จ');
          // ดำเนินการหลังจากลบบ้านสำเร็จ (เช่น อัปเดตรายการบ้าน)
          // loadHouses(); // เรียกโหลดรายการบ้านอีกครั้งหลังจากลบ
          // console.log(data)
        })
        // console.log(data)
        .catch(error => {
          console.error(error);
          alert('เกิดข้อผิดพลาดในการยกเลิกการจอง');
        });
    }
  };

  return (
    <div className="container">
      
      {Auth.isLoggedIn && (
        <div className="d-flex justify-content-end">
          <Link to="/AddBooking" className="btn btn-success">เพิ่ม</Link>
        </div>
      )}

<br/>
      <div>
        
        {books.map((book, idx) => (
          <div className="card" key={idx}>
             {Auth.isLoggedIn && (
                <div className="d-flex justify-content-end">
                    <button className="btn btn-warning" onClick={() => { navigate('/EditBooking'); }}>แก้ไข</button>
                </div>
            )}
            <div className="content">
              <p>เลขการจอง: {book.b_id}</p>
              <p>ราคา: {book.amount}</p>
              <p>วันที่จอง: {book.date}</p>
              <p>สถานะการจอง: {book.status}</p>
              <p>บ้านเลขที่: {book.h_id}</p>
              <p>เลขบัตรประชาชนผู้จอง: {book.user_id}</p>
            </div>
            <div className="content">

            {Auth.isLoggedIn && (
                <div className="content">
                 
                  <button className="btn btn-danger" onClick={() => handleDeleteClick(book)}>
                    ยกเลิกการจอง
                  </button>
                </div>

              )}

              <br />
              <br />
              <br />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default BookingCard;
