import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { DropdownButton, Dropdown } from 'react-bootstrap';

const HousingEdit = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [housePlan, setHousePlan] = useState('');
  const [numDeed, setNumDeed] = useState('');
  const [landArea, setLandArea] = useState('');
  const [area, setArea] = useState('');
  const [price, setPrice] = useState('');
  const [note, setNote] = useState('');
  const [image, setImage] = useState('');
  const [zoneId, setZoneId] = useState('');

  const [zones, setZones] = useState([]);

  useEffect(() => {
    loadHouseData();
    loadHouseTypes();
  }, []);

  const loadHouseTypes = () => {
    axios
      .get('http://26.90.237.200:3000/admin/house_zone/read')
      .then(response => {
        setZones(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const loadHouseData = () => {
    const data = { number: id };
    axios.post('http://26.90.237.200:3000/admin/house/read/number', data)
      .then(response => {
        const houseData = response.data;
        setHousePlan(houseData.house_plan || '');
        setNumDeed(houseData.num_deed || '');
        setLandArea(houseData.land_area || '');
        setArea(houseData.area || '');
        setPrice(houseData.h_price || '');
        setNote(houseData.h_note || '');
        setImage(houseData.image || '');
        setNewidFK(houseData.hz_id || '');
      })
      .catch(error => {
        console.error('เกิดข้อผิดพลาดในการดึงข้อมูล:', error);
      });
  };

  const EditHouse = async () => {
    const data = {
      no: id,
      h_id: numDeed,
      house_plan: housePlan,
      num_deed: numDeed,
      land_area: landArea,
      area: area,
      h_price: price,
      h_note: note,
      image: image,
      hz_id: zoneId,
    };

    try {
      const response = await axios.patch('http://26.90.237.200:3000/admin/edit', data);
      console.log(response.data);
      navigate('/Housing');
    } catch (error) {
      console.error(error);
      navigate('/Error');
    }
  };

  return (
    <div className="container">
      <form>
      <div className="mb-3">
  <div>
    <label htmlFor="id" className="form-label">
      รหัส
    </label>
    <input
      type="text"
      className="form-control"
      id="id"
      value={id}
      readOnly
    />
  </div>
  <div>
    <label htmlFor="newHousePlan" className="form-label">
      แปลนบ้าน
    </label>
    <input
      type="text"
      className="form-control"
      id="newHousePlan"
      value={newHousePlan}
      onChange={e => setHousePlan(e.target.value)}
    />
  </div>
  <div>
    <label htmlFor="newNumDeed" className="form-label">
      เลขที่โฉนด
    </label>
    <input
      type="text"
      className="form-control"
      id="newNumDeed"
      value={newNumDeed}
      onChange={e => setNumDeed(e.target.value)}
    />
  </div>
  <div>
    <label htmlFor="newLandArea" className="form-label">
      ขนาดพื้นที่
    </label>
    <input
      type="number"
      className="form-control"
      id="newLandArea"
      value={newLandArea}
      onChange={e => setLandArea(parseInt(e.target.value))}
    />
  </div>
  <div>
    <label htmlFor="newArea" className="form-label">
      พื้นที่ใช้สอย
    </label>
    <input
      type="number"
      className="form-control"
      id="newArea"
      value={newArea}
      onChange={e => setArea(parseInt(e.target.value))}
    />
  </div>
  <div>
    <label htmlFor="newPrice" className="form-label">
      ราคา
    </label>
    <input
      type="number"
      className="form-control"
      id="newPrice"
      value={newPrice}
      onChange={e => setPrice(parseInt(e.target.value))}
    />
  </div>
  <div>
    <label htmlFor="newNote" className="form-label">
      หมายเหตุ
    </label>
    <input
      type="text"
      className="form-control"
      id="newNote"
      value={newNote}
      onChange={e => setNote(e.target.value)}
    />
  </div>
  <div>
    <label htmlFor="newImage" className="form-label">
      รูปภาพ
    </label>
    <input
      type="file"
      className="form-control"
      id="newImage"
      onChange={e => setImage(e.target.files[0])}
    />
  </div>
  <div>
    <label htmlFor="newidFK" className="form-label">
      โซนบ้าน
    </label>
    <DropdownButton
      id="newidFK"
      title={newidFK !== '' ? zones.find(zone => zone.hz_id === newidFK)?.name : 'เลือกโซนบ้าน'}
      onSelect={e => setNewidFK(parseInt(e))}
    >
      {zones.map((zone, idx) => (
        <Dropdown.Item key={idx} eventKey={zone.hz_id}>
          {zone.name}
        </Dropdown.Item>
      ))}
    </DropdownButton>
  </div>
</div>
        <button type="button" className="btn btn-primary" onClick={EditHouse}>
          แก้ไข
        </button>
      </form>
    </div>
  );
};

export default HousingEdit;
