import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from "react-router-dom";
import dayjs from 'dayjs';

const Receipt = () => {
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [houses, setHouse] = useState([]);

  const { Bid } = useParams();

  useEffect(() => {
    loadBookingData();
    loadHouse();
  }, []);

  const loadBookingData = () => {
    axios.post(`http://26.90.237.200:3000/book/read/id`, { id: Bid })
      .then(response => {
        setBooks(response.data);
        // เมื่อได้รับข้อมูลการจองแล้ว ให้โหลดข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง
        if (response.data.length > 0) {
          loadUserData(response.data[0].user_id);
        }
      })
      .catch(error => {
        console.error(error);
      });
  };

  const loadUserData = (userId) => {
    axios.post(`http://26.90.237.200:3000/user/read/id`, { id: userId })
      .then(response => {
        setUsers(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };


  const loadHouse = (userId) => {
    axios.post('http://26.90.237.200:3000/admin/house/read/number', { id: userId })
      .then(response => {
        setHouse(response.data);
            })
      .catch(error => {
        console.error('เกิดข้อผิดพลาดในการดึงข้อมูล:', error);
      });
  };

  return (
    <div className="container">
      <br />
      <div>
        {books.map((book) => (
          <div className="card" key={book.b_id}>
            <div className="content">
              <p>เลขการจอง : {book.b_id}</p>
              {/* แสดงข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง */}
              {users.map((user) => (
                <div key={user.id}>
                  <p>ชื่อ-สกุล : {user.user_name} {user.user_lastname}</p>
                  <p>เบอร์โทร : {user.user_phone}</p>
                  <p>อายุ : {user.user_age}</p>
                </div>
              ))}
              <p>วันที่จอง : {dayjs(book.date).format('YYYY-MM-DD')}</p>
              <p>บ้านเลขที่ : {book.h_id}</p>
              <p>ขนาดพื้นที่ดิน : ตารางวา</p>
              <p>แปลนบ้าน :</p>
              <p>รวมเงินทั้งสิ้น : บาท</p>
            </div>
          </div>
        ))}
      </div>
      <br />
    </div>
  );
};

export default Receipt;
