import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
const navigate = useNavigate();

const TypeEdit = () => {
  const House_API = "http://26.90.237.200:3000/admin/house_type/edit";

  const [id, setId] = useState("");
  const [newName, setNewName] = useState("");
  const [newUsableArea, setNewUsableArea] = useState("");
  const [newStartPrice, setNewStartPrice] = useState("");

  const updateEmp = async () => {
    const data = {
      id: id,
      newName: newName,
      newUsableArea: newUsableArea,
      newStartPrice: newStartPrice
    };

    try {
      const response = await axios.patch(House_API, data);
      console.log(response.data);
      navigate('/');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <form>
        <div>
          <div>
            <label>รหัส</label>
            <input
              type="text"
              value={id}
              onChange={(e) => setId(e.target.value)}
            />
          </div>
          <div>
            <label>ชื่อใหม่</label>
            <input
              type="text"
              value={newName}
              onChange={(e) => setNewName(e.target.value)}
            />
          </div>
          <div>
            <label>พื้นที่ใช้สอยใหม่</label>
            <input
              type="text"
              value={newUsableArea}
              onChange={(e) => setNewUsableArea(e.target.value)}
            />
          </div>
          <div>
            <label>ราคาเริ่มต้นใหม่</label>
            <input
              type="text"
              value={newStartPrice}
              onChange={(e) => setNewStartPrice(e.target.value)}
            />
          </div>
        </div>

        <button type="button" onClick={updateEmp}>
          แก้ไขข้อมูล
        </button>
      </form>
    </div>
  );
};

export default TypeEdit;
