    import React, { useState,useEffect } from 'react';
    import axios from 'axios';
    import { useNavigate } from "react-router-dom";
    import { Link } from 'react-router-dom';
    import { NumericFormat } from 'react-number-format';

    const AddBooking = () => {
        const navigate = useNavigate();
        const HOUSE_API = 'http://26.90.237.200:3000/book/add';
        const USER_API = "http://26.90.237.200:3000/user/edit-user";
    
        const [addName, setAddName] = useState('');
        const [addLastname, setAddLastname] = useState('');
        const [addAddress, setAddAddress] = useState('');
        const [addAge, setAddAge] = useState('');
        const [addPhone, setAddPhone] = useState('');
        const [addHouseFK, setAddHouseFK] = useState('');
        const [addUserFK, setAddUserFK] = useState('');
        const [addNationality, setAddNationality] = useState('');
        const [houses, setHouses] = useState([]);
        const [selectedHouse, setSelectedHouse] = useState(null);
    
        useEffect(() => {
            loadHouses();
        }, []);
    
        const loadHouses = async () => {
            try {
                const response = await axios.get('http://26.90.237.200:3000/admin/house/read');
                setHouses(response.data);
            } catch (error) {
                console.error('Failed to load houses:', error);
            }
        };
    
        console.log(selectedHouse);
    
        useEffect(() => {
            if (addHouseFK) {
                const house = houses.find(house => house.h_id === addHouseFK);
                setSelectedHouse(house);
            } else {
                setSelectedHouse(null);
            }
        }, [addHouseFK, houses]);
    
        const AddBook = async () => {
            if (!addName || !addLastname || !addAddress || !addAge || !addPhone || !addHouseFK || !addUserFK || !addNationality) {
                alert('กรุณากรอกข้อมูลให้ครบถ้วน');
                return;
            }
    
            if (isNaN(addPhone)) {
                alert('กรุณากรอกเบอร์โทรศัพท์เป็นตัวเลขเท่านั้น');
                return;
            }
    
            const confirmBooking = window.confirm('คุณต้องการทำการจองใช่หรือไม่?');
            if (!confirmBooking) {
                return;
            }
    
            const user_data = {
                addName: addName,
                addLastname: addLastname,
                addAddress: addAddress,
                addAge: addAge,
                addPhone: addPhone,
                addHouseFK: addHouseFK,
                addUserFK: addUserFK,
                addNationality: addNationality,
            };
    
            const house_data = {
                newEmail: addPhone,
                newPassword: addUserFK,
            };
    
            try {
                await axios.post(USER_API, user_data);
                alert('ทำการจองสำเร็จแล้ว');
            } catch (error) {
                console.error('Failed to post user data:', error);
                alert('ทำการจองผิดพลาด');
                return;
            }
    
            try {
                const response = await axios.post(HOUSE_API, house_data);
                alert('เพิ่มข้อมูลสำเร็จ');
                navigate(`/Receipt/${response.data.id}`);
            } catch (error) {
                console.error('Failed to post house data:', error);
                alert('เกิดข้อผิดพลาดเพิ่มข้อมูล');
            }
        };
    
            
          return (
        
        <div className="container">
               <h1>เพิ่มการจอง</h1>
               <div className="form-group">
            <label>เลือกบ้าน</label>
            <select className="form-control"value= {addHouseFK} onChange={(e) => setAddHouseFK(e.target.value)}>
        <option value="">-- บ้านเลขที่ --</option>
        {houses.filter(house => ![1,2,3].includes(house.status)).map((house) => (
            <option key={house.id} value={house.id}>
                {house.h_id}
            </option>
        ))}
            </select>
            <br/>
           { selectedHouse && (
                <div className="card container" >
                    <br/>
                    <h4>ข้อมูลบ้านที่จอง</h4>
                    <br/>
                    <p>บ้านเลขที่: { selectedHouse.h_id }</p>
                    <p>แปลนบ้าน: { selectedHouse.house_plan }</p>
                    <p>บ้านเลขที่: { selectedHouse.num_deed }</p>
                    <p>บ้านเลขที่: { selectedHouse.num_survey }</p>
                    <p>ขนาดพื้นที่ดิน: { selectedHouse.land_area } ตารางวา</p>
                    <p>ขนาดพื้นที่ใช้สอย: { selectedHouse.area } ตารางเมตร</p>
                    <NumericFormat
                value={selectedHouse.h_price}
                allowLeadingZeros
                thousandSeparator=","
                displayType="text"
                renderText={(value) => <p>ราคาบ้าน {value} บาท</p>}
              />
                  <NumericFormat
                value={selectedHouse.book_price}
                allowLeadingZeros
                thousandSeparator=","
                displayType="text"
                renderText={(value) => <p> ราคาจอง {value} บาท</p>}
              />
                    <p>หมายเหตุ: { selectedHouse.h_note }</p>
                </div>
            )}
        
            </div>
        <form >
            <div className="mb-3">
                <br/>
            <div className="card container">
            <br/>
                <h4>ข้อมูลลูกค้า</h4>
                <br/>
            <div>
                <label htmlFor="id" className="form-label">
                เลขบัตรประชาชน
                </label>
                <input
                type="text"
                className="form-control"
                maxLength="13"
                id="id"
                value={addUserFK}
                onChange={(e) => setAddUserFK(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="addName" className="form-label">
                ชื่อ
                </label>
                <input
                type="text"
                maxLength="50"
                className="form-control"
                id="addName"
                value={addName}
                onChange={(e) => setAddName(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="addLastname" className="form-label">
                นามสกุล
                </label>
                <input
                type="text"
                maxLength="50"
                className="form-control"
                id="addLastname"
                value={addLastname}
                onChange={(e) => setAddLastname(e.target.value)}
                />
            </div>
            <div >
                <label htmlFor="addAddress" className="form-label">
                ที่อยู่
                </label>
                <input
                type="text"
                maxLength="250"
                className="form-control"
                id="addAddress"
                value={addAddress}
                onChange={(e) => setAddAddress(e.target.value)}
                />
            </div>
            
            <div>
                <label htmlFor="addAge" className="form-label">
                อายุ
                </label>
                <input
                type="text"
                className="form-control"
                maxLength="3"
                id="addAge"
                value={addAge}
                onChange={(e) => setAddAge(e.target.value)}
                />
            </div>

            <div>
                <label htmlFor="addNationality" className="form-label">
                สัญชาติ
                </label>
                <input
                type="text"
                maxLength="10"
                className="form-control"
                id="addNationality"
                value={addNationality}
                onChange={(e) => setAddNationality(e.target.value)}
                />
            </div>

            <div>
                <label htmlFor="addPhone" className="form-label">
                เบอร์โทร
                </label>
                <input
                type="text"
                className="form-control"
                id="addPhone"
                maxLength="10"
                value={addPhone}
                onChange={(e) => setAddPhone(e.target.value)}
                />
            </div>
            <br/>
            </div>
            </div>

{/* 
            <div className="card container" >
            <br/>
                <h4>ข้อมูลบ้านที่จอง</h4>
                <br/>

            <div className="form-group">
            <label>บ้านเลขที่</label>
            <input
                type="text"
                className="form-control"
                value={addHouseFK}
                onChange={(e) => setAddHouseFK(e.target.value)}
            />
            </div>
            </div> */}

            <br/>
                <div className="content">
                    <Link  onClick={AddBook} className="btn btn-primary">  จอง </Link>
                </div>
            <br/>
        </form>
        </div>
    );
    };
    export default AddBooking;
