import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from "react-router-dom";
import dayjs from 'dayjs';
import 'dayjs/locale/th';
import customParseFormat from 'dayjs/plugin/customParseFormat';
import { NumericFormat } from 'react-number-format';
import { useNavigate } from 'react-router-dom';

dayjs.extend(customParseFormat);
dayjs.locale('th'); 

const Bill = () => {
  const [book, setBooks] = useState([]);
  const [user, setUsers] = useState([]);
  const [profile, setProfile] = useState([]);
  const { Bid } = useParams();

  const [house, setHouse] = useState([]);
  const [zone, setZones] = useState([]);

  useEffect(() => {
    loadBookingData();
  }, []);
  
  const loadBookingData = () => {
    axios.post(`http://26.90.237.200:3000/book/read/id`, { id: Bid })
      .then(response => {
        setBooks(response.data);
        // เมื่อได้รับข้อมูลการจองแล้ว ให้โหลดข้อมูลผู้ใช้ที่เกี่ยวข้องกับข้อมูลการจอง
        if (response.data.length > 0) {
          loadUserData(response.data[0].user_id);
        //   loadHouse(response.data[0].h_id);
          // console.log(response.data[0].h_id)
        }
      })
      .catch(error => {
        console.error(error);
      });
  };
  
//   const loadHouse = (houseId) => {
//     axios.post(`http://26.90.237.200:3000/admin/house/read/id`, { id: houseId })
//       .then(response => {
//         setHouse(response.data);
//         // console.log(response.data)
//       })
//       .catch(error => {
//         console.error('เกิดข้อผิดพลาดในการดึงข้อมูล:', error);
//       });
//   };
  
  useEffect(() => {
    axios.get('http://26.90.237.200:3000/admin/read')
      .then(response => {
        setProfile(response.data);
      })
      .catch(error => {
        console.error(error);
      });

  }, []);

  const loadUserData = (userId) => {
    axios.post(`http://26.90.237.200:3000/user/read/id`, { id: userId })
      .then(response => {
        setUsers(response.data);
        // console.log(response.data)
      })
      .catch(error => {
        console.error(error);
      });
  };

  useEffect(() => {
    loadHouses();
    loadHouseTypes();
  }, []);

  const loadHouses = () => {
    axios
      .get('http://26.90.237.200:3000/admin/house/read')
      .then(response => {
        setHouse(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const loadHouseTypes = () => {
    axios
      .get('http://26.90.237.200:3000/admin/house_zone/read')
      .then(response => {
        setZones(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const date = dayjs();
  const day = date.format('DD');
  const month = date.format('MMMM');
  const year = parseInt(date.format('YYYY')) + 543;
  const bookingDate = `${day} ${month} ${year}`;
  
  return (
    <div className="container">
      <br />
      {house.map((zone, idx) => (
      <div>
      <h1> ค่าส่วนกลาง </h1>
          <div className="card" >
            <div className="content">
       
         
            </div>
          </div>
      </div>
      ))}
      <br />
    
    </div>
  );
};

export default Bill;
