import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ZoneAdd = () => {
  const navigate = useNavigate();
  const House_API = 'http://26.90.237.200:3000/admin/house_type/add';

  const [addName, setAddName] = useState('');

  const addHouse = async () => {
    const data = {
      addName: addName,
    };

    try {
      const response = await axios.post(House_API, data);
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
    navigate('/Housing');
  };

  return (
    <div className="container">
      <form>
        <div className="mb-3">
   
          <div>
            <label htmlFor="addName" className="form-label">
              โซน
            </label>
            <input
              type="text"
              className="form-control"
              id="addName"
              value={addName}
              onChange={(e) => setAddName(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="addUsableArea" className="form-label">
              พื้นที่ใช้สอย
            </label>
            <input
              type="text"
              className="form-control"
              id="addUsableArea"
              value={addUsableArea}
              onChange={(e) => setAddUsableArea(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="addLandArea" className="form-label">
              ราคา
            </label>
            <input
              type="number"
              className="form-control"
              id="addLandArea"
              value={addStartPrice}
              onChange={(e) => setAddStartPrice(parseInt(e.target.value))}
            />
          </div>
         
        </div>
        <button type="button" className="btn btn-primary" onClick={addHouse}>
          เพิ่มบ้าน
        </button>
      </form>
    </div>
  );
};

export default ZoneAdd;
