import React,  { useContext } from 'react';
import { AuthContext } from '../Login/AuthContext';
// import './ShowHousing.css'
import '../../image//464.jpg';
import useFetch from '../../useFetch';

import { NumericFormat } from 'react-number-format';
import { useNavigate } from 'react-router-dom';

const ShowHousing = () => {
  const { status } = useContext(AuthContext);
const House_API = "http://26.90.237.200:3000/admin/house_type/read";
const { data } = useFetch(House_API);
const navigate = useNavigate();

        return (
          <div className="container">
          <div className="">
            {data.map((val) => (
              <div className="card" key={val.ht_id}>
                <div className="content">
                  <p>โมเดล {val.name}</p>
                  <p>ขนาดพื้นที่ {val.usable_area} ตารางเมตร</p>
                  <NumericFormat
                    value={val.start_price}
                    allowLeadingZeros
                    thousandSeparator=","
                    displayType="text"
                    renderText={(value) => <p>{value} บาท</p>}
                  />
                </div>
    
                <div className="content">
                  <p>IMG อันนี้จะใส่เป็นรูปบ้านนะแต่ยังไม่ได้ทำ</p>
                </div>
    
                <div className="content">
                  <button
                    className="btn btn-primary"
                    onClick={() => {
                      navigate('/Planner');
                    }}
                  >
                    ดูรายละเอียด
                  </button>
                  <br />
                  {status && (
              <div className="content">
                {/* ส่วนที่ต้องการซ่อนเมื่อยังไม่ได้เข้าสู่ระบบ */}
                <button
                  className="btn btn-primary"
                  onClick={() => {
                    navigate('/HousingEdit');
                  }}
                >
                  แก้ไข
                </button>
              </div>
            )}
                </div>
              </div>
            ))}
          </div>
        </div>
   
        )        

  }

  export default ShowHousing;
