import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../Login/AuthContext';
import { PDFDocument, rgb, StandardFonts } from 'pdf-lib';

const Receipt = () => {
  const [books, setBooks] = useState([]);
  const Auth = useContext(AuthContext);

  useEffect(() => {
    loadBooking();
  }, []);

  const loadBooking = () => {
    axios.get('http://26.90.237.200:3000/book/read')
      .then(response => {
        setBooks(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const generatePdf = async () => {
    try {
      const pdfDoc = await PDFDocument.create();
      const page = pdfDoc.addPage([600, 800]);
      const font = await pdfDoc.embedFont(StandardFonts.Helvetica);
      let y = 780;

      books.forEach((book, idx) => {
        page.drawText(`เลขการจอง: ${book.b_id}`, {
          x: 50,
          y: y,
          size: 12,
          font: font, // ใช้ตัวแปร font ที่เป็นตัวแปรของ PDFFont
          color: rgb(0, 0, 0),
        });

        page.drawText(`ราคา: ${book.amount}`, {
          x: 50,
          y: y - 20,
          size: 12,
          font: StandardFonts.Helvetica,
          color: rgb(0, 0, 0),
        });

        page.drawText(`วันที่จอง: ${book.date}`, {
          x: 50,
          y: y - 40,
          size: 12,
          font: StandardFonts.Helvetica,
          color: rgb(0, 0, 0),
        });

        page.drawText(`บ้านเลขที่: ${book.h_id}`, {
          x: 50,
          y: y - 60,
          size: 12,
          font: StandardFonts.Helvetica,
          color: rgb(0, 0, 0),
        });

        page.drawText(`เลขบัตรประชาชนผู้จอง: ${book.user_id}`, {
          x: 50,
          y: y - 80,
          size: 12,
          font: StandardFonts.Helvetica,
          color: rgb(0, 0, 0),
        });

        y -= 120;
      });

      const pdfBytes = await pdfDoc.save();
      const pdfUrl = URL.createObjectURL(new Blob([pdfBytes], { type: 'application/pdf' }));

      // แสดงหน้าต่างใหม่เพื่อดาวน์โหลดไฟล์ PDF
      window.open(pdfUrl, '_blank');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container">
      <br />
      <div>
        {books.map((book, idx) => (
          <div className="card" key={idx}>
            <div className="content">
              <p>เลขการจอง: {book.b_id}</p>
              <p>ราคา: {book.amount}</p>
              <p>วันที่จอง: {book.date}</p>
              <div></div>
              <p>บ้านเลขที่: {book.h_id}</p>
              <p>เลขบัตรประชาชนผู้จอง: {book.user_id}</p>
            </div>
            <div className="content">
              <button type="button" className="btn btn-primary" onClick={generatePdf}>
                บันทึกเป็น PDF
              </button>
              <br />
              <br />
              <br />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Receipt;
