import React from 'react';
import './Login.css';
import { useState } from 'react';

const Login = (porps) => {
  const [jsonData, setjsonData] = useState({username:'', password: '' })
  const handleInput = (event) => {
    const { name, value } = event.target;
    setjsonData({...jsonData,[name]:value })
    // console.log(jsonData);
  }
  const handleSubmit = (event) => {
    event.preventDefault();
    // console.log(jsonData);
  }

    fetch('http://26.90.237.200:3000/login', {
      method: 'POST',
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(jsonData),
    })
    .then(response => response.json())
    .then(data => {
      if (data.status == 'ok')
      console.log('Success', data);
      })
      .catch((error) => {
        console.error('',error)
    });

    return (
    <div className="">

<form class="form-control">
  <div class="">
    <label for="username">Username</label>
    <input type='username' name='username' value={ jsonData.username} onChange={handleInput} class="form-control" required/>
  </div>
  <div class="">
    <label for="password">Password</label>
    <input type='password' name='password' value={ jsonData.password } onChange={handleInput} class="form-control" required/>
  </div>
  <button className="button" type="submit" onClick={handleSubmit} >Login</button>
</form>
    </div>
    );
  }

  export default Login;
