import React, { useState } from 'react';
import axios from 'axios';

const config = {
  headers: {
    'Authorization': `Bearer ${token}`,
    'Content-Type': 'application/json'
  }
};

const LoginForm = () => {
  const [authStatus, setAuthStatus] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://26.90.237.200:3000/login", { username, password });
      const token = response.data.token;
      localStorage.setItem('token', token);
      console.log(response.data);

      const authResponse = await axios.post('http://26.90.237.200:3000/authen', { username, password });
      console.log(authResponse.data);
      if (authResponse.data.decoded.statusAuthen === 1) {
        alert('Authen success');
        setAuthStatus(authResponse.data.decoded.statusAuthen);
      } else {
        alert('Authen failed');
      }
    } catch (error) {
      alert('Authen error');
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Login Form</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Username</label>
          <input
            type="text"
            name="username"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="password">Password</label>
          <input
            type="password"
            name="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div>
          <button type="submit">Login</button>
        </div>
      </form>
      {authStatus === 1 && <p>Authentication status: Success</p>}
      {authStatus === 0 && <p>Authentication status: Failed</p>}
    </div>
  );
};

export default LoginForm;
