import React from 'react';
import './Login.css';
import { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = (props) => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const [authStatus, setAuthStatus] = useState(null); // เพิ่มตัวแปร state สำหรับเก็บค่า statusAuthen
  // Handle validations
  // Set the request headers with the authentication token
  const config = {
    headers: {
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'application/json'
    }
  };

  const handleSubmit = e => {
    e.preventDefault();
    
    if (!username || !password) {
      alert('Please enter both username and password.');
      return;
    }
  
    axios.post("http://26.90.237.200:3000/login", { username, password })
      .then(response => {
        const token = response.data.token;
        localStorage.setItem('token',token);
        // console.log(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  
    axios.post('http://26.90.237.200:3000/authen', { username, password }, config)
      .then(response => {
        console.log(response.data);
        if (response.data.decoded.statusAuthen === 1) {
          alert('Authen success');
          navigate('/Housing');
          // เก็บค่า statusAuthen ในตัวแปร state
          setAuthStatus(response.data.decoded.statusAuthen);
        } else {
          alert('Authen failed');
        }
       
      })
      .catch(error => {
        alert('Authen error');
        console.error(error);
      });
  };
  
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  


  return (
    <div>
      <form id="login" onSubmit={handleSubmit}>
        <h1>Login</h1>
        <p className="item">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            name="username"
            id="username"
            value={username}
            onChange={e => setUsername(e.target.value)}
          />
        </p>
        <p className="item">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            name="password"
            id="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />
        </p>
        <p className="item">
          <input type="submit" value="Login" />
        </p>
      </form>
    </div>
  );
};

export default Login;
